import styles from "../styles/card.module.scss";

interface Props {
  title: string;
  desc: string;
  cover: string;
}

const Card = (props: Props) => {
  return (
    <div className={styles.card}>
      <img src={props.cover} alt="" className={styles.cover} />
      <h3>{props.title}</h3>
      <p>{props.desc}</p>
    </div>
  );
};

export default Card;
