interface Props {
  title: string;
  desc: string;
  cover: string;
}

const Card = (props: Props) => {
  return (
    <div>
      <img src={props.cover} alt="" className="cover" />
      <h3>{props.title}</h3>
      <p>{props.desc}</p>
    </div>
  );
};

export default Card;
